import React, {Component} from 'react';
import Header from './header';
import Body from './body';
import axios from 'axios';
import headerImg from '../images/postit.jpg';
import Form from './form';

class Main extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[]
		}
		this.deleteItem=this.deleteItem.bind(this);
		this.createItem=this.createItem.bind(this);
	};

	componentDidMount(){
		axios.get('http://localhost:3001/posts_api')
		.then(res=>{
			const results=res.data;
			this.setState({data:results})
		})
		// console.log(this.state.data);
	};

	deleteItem=(id) =>{
		axios.delete(`http://localhost:3001/posts/${id}.json`)
		.then(res=>{
		  var newData = this.state.data.filter((d) => d.id !==id)
		  this.setState({data: newData});
		})
		.catch(error => console.log(error))
		console.log(id)
	}

	createItem=(t,d,p)=>{
		let newFields=JSON.stringify({title:t,description:d,date:p})
		axios.post('http://localhost:3001/posts',{newFields})
		.then(res=>{
			this.setState({data:this.state.data.concat(newFields)})

		})
	}

	render(){

		const divRightStyle={
			'display':'inline-block',
			'width':'70%',
			'verticalAlign':'top'
		}
		const divLeftStyle={
			'display':'inline-block',
			'width':'30%',
			'verticalAlign':'top'
		}

		return(
			<div>
				<Header headerImg={headerImg}/>
				<div style={divRightStyle}>
					<Body data={this.state.data} deleteItem={this.deleteItem}/>
				</div>
				<div style={divLeftStyle}>
					<Form />
				</div>
			</div>
		)
	}
};

export default Main;
