import React, {Component} from 'react';
// import {BootstrapTable} from 'react-bootstrap-table';


class Body extends Component{
	render(props){
		const tableStyle={
			'width':'55%',
			'padding':'5px',
			'borderRadius':'5px',
			'border':'1px solid #F9F9F9'
		}
		const trTdStyle={
			'border':'1px solid #EEE'
		}
		const tbodyStyle={
			'textAlign':'center'
		}
		const results=this.props.data;
		const tableHeader=['Title','Description','Date Posted'];
		console.log(results);
		return(
			<table style={tableStyle}>
				<thead>
					<tr>
						{tableHeader.map(function(k,i){
							return <th key={i}>{k}</th>
						})}
					</tr>
				</thead>
				<tbody style={tbodyStyle}>
					 {results.map(item => <tr key={item.id}><td>{item.title}</td><td>{item.description}</td><td>{item.date_posted}</td></tr>)}
				</tbody>
			</table>
		)
	}
};

export default Body;
