import React, {Component} from 'react';
import Header from './header';
import Body from './body';
import axios from 'axios';
import headerImg from '../images/postit.jpg';
import CreateForm from './createForm';
import UpdateForm from './updateForm';

export default class Main extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[],
			showItem:false,

			title:'',
			description:'',
			date_posted:''

		}
		this.deleteItem=this.deleteItem.bind(this);
		this.createItem=this.createItem.bind(this);
		this.showUpdateForm=this.showUpdateForm.bind(this);
		this.updateItem=this.updateItem.bind(this);
	};

	componentDidMount(){
		axios.get('http://localhost:3001/posts_api')
		.then(res=>{
			const results=res.data;
			this.setState({data:results})
		})
	};

	showUpdateForm=(id) =>{
		let temptitle:'';
		let tempdescr:'';
		let tempdate:'';
		this.state.data.map(function(k){
			if(k.id === id){
				temptitle=k.title
				tempdescr=k.description
				tempdate=k.date_posted
			};
		})
		this.setState({
			showItem:true,
			title:temptitle,
			description:tempdescr,
			date_posted:tempdate
		})
	}	

	updateItem=(id,title,description,date_posted) =>{
		var updateItems={id:id,title:title,description:description,date_posted:date_posted};
		var stringified=JSON.stringify(updateItems);
		axios.put(`http://localhost:3001/posts/${id}.json`,stringified,{headers: {'Content-Type':'application/json'}})
		.then(res =>{
			let newData=this.state.data.filter((i) => i.id !== id)
			newData.push(stringified)
			this.setState({data:newData,showItem:false})
		})
	}


	deleteItem=(id) =>{
		axios.delete(`http://localhost:3001/posts/${id}.json`)
		.then(res=>{
		  var newData = this.state.data.filter((d) => d.id !==id)
		  this.setState({data: newData});
		})
		.catch(error => console.log(error))
		console.log(id)
	}

	createItem=(title,description,date_posted)=>{
		let fields={
			title:title,
			description:description,
			date_posted:date_posted
		}
		let strf=JSON.stringify(fields)
		axios.post(`http://localhost:3001/posts`,strf, {headers: {'Content-Type': 'application/json'}})
		.then(res=>{
			console.log(res);
			this.setState({
				data:this.state.data.concat(fields)
			})
		})
		.catch(error=>{
			console.log(error)
		})
	}


	render(){
		const divRightStyle={
			'display':'inline-block',
			'width':'80%',
			'verticalAlign':'top'
		}
		const divLeftStyle={
			'display':'inline-block',
			'width':'20%',
			'verticalAlign':'top'
		}
		const hide={
			'display':'none'
		}
		const show={
			'display':''
		}

		return(
			<div>
				<Header headerImg={headerImg}/>
				<div style={divRightStyle}>
					<Body data={this.state.data} deleteItem={this.deleteItem} showUpdateForm={this.showUpdateForm}/>
				</div>
				<div style={divLeftStyle}>
					<div>
						<CreateForm createItem={this.createItem}/>
					</div>
					<br />
					<div style={this.state.showItem === true ? show : hide} >
						<UpdateForm title={this.state.title} description={this.state.description} date_posted={this.state.date_posted} id={this.state.id} />
					</div>
				</div>
			</div>
		)
	}
};
