import React, {Component} from 'react';

export default class UpdateForm extends Component{
	constructor(props){
		super(props);
		this.state={
			newTitle:'',
			newDescr:'',
			newDate:''
		}

		this.handleUpdate=this.handleUpdate.bind(this);
		this.handleTitle=this.handleTitle.bind(this);
		this.handleDescr=this.handleDescr.bind(this);
		this.handleDate=this.handleDate.bind(this);
	}

	handleTitle=(e) =>{
		this.setState({newTitle:e.target.value})
	}

	handleDescr=(e) =>{
		this.setState({newDescr:e.target.value})
	}

	handleDate=(e) =>{
		this.setState({newDate:e.target.value})
	}

	handleUpdate=(e) =>{
		e.preventDefault();
		this.props.updateItem(this.props.id,this.state.newTitle,this.state.newDescr,this.state.newDate)
	};

	render(props){
		console.log(props)
		return(	

			<form>
				Title: <input type='text' name='' value={this.state.newTitle} placeholder={this.props.title} onChange={this.handleTitle} /><br />
				Description: <input type='text' name='' value={this.state.newTitle} placeholder={this.props.description} onChange={this.handleDescr} /><br />
				Date Posted: <input type='date' name='' value={this.state.newTitle} placeholder={this.props.date_posted} onChange={this.handleDate} />
				<button onClick={()=>this.handleUpdate}>Update</button>
			</form>
		)
	}
};
