import React, {Component} from 'react';

export default class Form extends Component{
	constructor(props){
		super(props);
		this.state={
			email:'',
			description:'',
			title:''
		}
		this.handleForm = this.handleForm.bind(this);
	}

	handleForm(e){
		e.preventDefault();
		console.log(e)
	}

	render(){
		return(
			<div>
				<form>
					Title: <input type='text' id='titleForm' value={this.state.title} onChange={this.handleTitle}/><br/>
					Description <input type='text' id='descForm' value={this.state.description} onChange={this.handleTitle}/><br/>
					Date Posted <input type='date' id='dateForm' value={this.state.date} onChange={this.handleTitle}/><br/>	
					<button onClick={()=> this.handleForm} type='submit'>Submit</button>
				</form>
			</div>
		)
	}
}
