import React, {Component} from 'react';
import axios from 'axios';

class Body extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[]
		}
		this.deleteItem=this.deleteItem.bind(this);
	}
	render(props){
		const tableStyle={
			'width':'55%',
			'padding':'5px',
			'borderRadius':'5px',
			'border':'1px solid #F9F9F9'
		}
		const trTdStyle={
			'border':'1px solid #EEE'
		}
		const tbodyStyle={
			'textAlign':'center'
		}
		const results=this.props.data;
		const tableHeader=['Title','Description','Date Posted','Action'];
		console.log(results);

		function removeItem(id){
			axios.delete('http://localhost:3001/posts_api/'+id)
			.then(res => {
				const data=this.state.data.filter(
					data=>data.id !== id
				)
				this.setState({data})
			})
			.catch(error => console.log(error))
		}

		return(
			<table style={tableStyle}>
				<thead>
					<tr style={trTdStyle}>
						{tableHeader.map(function(k,i){
							return <th style={trTdStyle} key={i}>{k}</th>
						})}
					</tr>
				</thead>
				<tbody style={tbodyStyle}>
					 {results.map(item => <tr style={trTdStyle} key={item.id}><td style={trTdStyle}>{item.title}</td><td style={trTdStyle}>{item.description}</td><td style={trTdStyle}>{item.date_posted}</td><td style={trTdStyle}><button>New</button><button onClick={this.removeItem(item.id)}>Delete</button><button>Update</button></td></tr>)}
				</tbody>
			</table>
		)
	}
};

export default Body;
