import React, {Component} from 'react';
import Header from './header';
import Body from './body';
import axios from 'axios';
import headerImg from '../images/postit.jpg';

class Main extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[]
		}
		this.deleteItem=this.deleteItem.bind(this);
		this.deletePermanently=this.deletePermanently.bind(this);
	};

	componentDidMount(){
		axios.get('http://localhost:3001/posts_api.json')
		.then(res=>{
			const results=res.data;
			this.setState({data:results})
		})
		// console.log(this.state.data);
	}

	deleteItem=(id)=>{
		console.log(id);
		axios.delete(`http://localhost:3001/posts/${id}`)
		.then(res=>{
			this.deletePermanently(id)
		})
		.catch(error => console.log(error))
	}

	deletePermanently=(id)=>{
	  var newData = this.state.data.filter((d) => d.id !==id)
	  this.setState({
	    data: newData
	  })
	}



	render(){
		return(
			<div>
				<Header headerImg={headerImg}/>
				<Body data={this.state.data} deleteItem={this.deleteItem}/>
			</div>
		)
	}
};

export default Main;
